(function(){tinymce.PluginManager.requireLangPack('cms_multimedia');tinymce.create('tinymce.plugins.CmsMultimediaPlugin',{init:function(ed,url){ed.addCommand('cmsMultimedia',function(){ed.windowManager.open({file:url+'/dialog.php',width:640,height:480,inline:1},{plugin_url:url,some_custom_arg:'custom arg'})});ed.addButton('cms_multimedia',{title:'cms_multimedia.desc',cmd:'cmsMultimedia',image:url+'/img/multimedia.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('cms_multimedia',n.nodeName=='IMG')})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'Cms Multimedia insertion plugin',author:'CeSPI | UNLP',authorurl:'https://desarrollo.cespi.unlp.edu.ar',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/example',version:"0.1"}}});tinymce.PluginManager.add('cms_multimedia',tinymce.plugins.CmsMultimediaPlugin)})();
